/**
 * Blur Placeholder Data URLs
 * 
 * Pre-generated base64 blur placeholders for critical images.
 * These tiny images are inlined to prevent layout shift while loading.
 * 
 * Generated using sharp library with 10x10px resize + blur.
 * 
 * @module data/blurPlaceholders
 */

/**
 * Generic blur placeholder (warm gray tone matching site aesthetic)
 * Used as fallback when specific placeholder not available.
 */
export const genericBlurPlaceholder =
    'data:image/jpeg;base64,';

/**
 * Hero bathroom blur placeholder
 */
export const heroBathroomBlur =
    'data:image/jpeg;base64,';

/**
 * Hero kitchen blur placeholder
 */
export const heroKitchenBlur =
    'data:image/jpeg;base64,';

/**
 * Product showcase blur placeholder
 */
export const productShowcaseBlur =
    'data:image/jpeg;base64,';

/**
 * Category placeholder (used for all categories)
 */
export const categoryBlur =
    'data:image/jpeg;base64,';

/**
 * Product card placeholder
 */
export const productBlur =
    'data:image/jpeg;base64,';

/**
 * Blur placeholders by image path
 */
export const blurPlaceholders: Record<string, string> = {
    '/images/hero/hero-bathroom.webp': heroBathroomBlur,
    '/images/hero/hero-kitchen.webp': heroKitchenBlur,
    '/images/hero/product-showcase.webp': productShowcaseBlur,
    '/images/categories/sanitaire.webp': categoryBlur,
    '/images/categories/meubles-sdb.webp': categoryBlur,
    '/images/categories/carrelage.webp': categoryBlur,
    '/images/categories/luminaire.webp': categoryBlur,
    '/images/categories/outillage.webp': categoryBlur,
};

/**
 * Get blur placeholder for an image path
 * Falls back to generic placeholder if not found
 */
export function getBlurPlaceholder(imagePath: string): string {
    return blurPlaceholders[imagePath] || genericBlurPlaceholder;
}
